(function(){var imageUploadMacros=$('div[data-module="image-upload"]')
setTimeout(function(){$.each(imageUploadMacros,function(id,macro){macro=$(macro)
var labels=macro.find('label')
var inputs=macro.find('input')
var labelText=''
$.each(labels,function(id,label){label=$(label)
if(id===0){labelText=label.text()}else{label.text(labelText)}
var labelForText=label.attr('for')
var fieldText=''
if(labelForText==='field-image-url'){fieldText=macro.attr('data-module-field_url')}else if(labelForText==='field-image-upload'){fieldText=macro.attr('data-module-field_upload')}
label.attr('for',fieldText)})
$.each(inputs,function(id,input){input=$(input)
var inputId=input.attr('id')
var fieldText=''
if(inputId==='field-image-url'){fieldText=macro.attr('data-module-field_url')}else if(inputId==='field-image-upload'){fieldText=macro.attr('data-module-field_upload')}
input.attr('id',fieldText)})})},500)})()